import{_ as t,c as a,o,a as e}from"./entry.1d9cc7c0.js";const n=""+new URL("pokemonunlimited.b8af096a.png",import.meta.url).href,s={},r=e("h2",null,"Project 4: Pokémon Unlimited",-1),d=e("p",null," For my final project, I was allocated 1 week. I decided to work solo because I wanted to have a hand in every part of its development. Paying tribute to a franchise from my childhood, Pokémon, the project used a database to store user-created Pokémon that could then be implemented into a grid-based game. I wanted to create something that was both fun and complex, testing my abilities and drawing on everything I had learned so far. I created the back-end database in PostgreSQL and managed it with a Restful API using a Python-based framework called Django. I then installed React, a JavaScript-based framework, for the front end and used Axios to make database requests.  ",-1),i=e("a",{href:"http://bit.ly/p4readme",target:"_blank",rel:"noopener noreferrer",class:"text-white"},"See GitHub ReadMe",-1),l=e("p",null,null,-1),c=e("p",null,[e("img",{className:"img-thumbnail mx-auto d-block projectImage",alt:"pokemonunlimited",src:n})],-1),m=[r,d,i,l,c];function h(u,_){return o(),a("div",null,m)}const f=t(s,[["render",h]]);export{f as default};
